// epk.js
import Link from 'next/link';
import groq from 'groq';
import client from '../client';
import BlockContent from '@sanity/block-content-to-react'

//styles
//import './epk.sass';

const EPK = (props) => {
  const {epkdata = {}} = {
    title = 'Missing title',
    body = []
  } = props;

  return (
    <div>
      title {title}
      <BlockContent blocks={body}/>
    </div>

  );
}
const query = groq`*[_type == "epk"]{
  name,
  title,
  body
}`;

EPK.getInitialProps = async () => ({ 
  epkdata: await client.fetch(query)
});

export default EPK;
