// epk.js
import Link from 'next/link';
import groq from 'groq';
import client from '../client';
import BlockContent from '@sanity/block-content-to-react'
import styles from './epk.module.scss';
import imageUrlBuilder from '@sanity/image-url';



const EPK = (props) => {
  const {
    title = 'Missing title',
    body = [],
    pressphotos = [],
  } = props;

  return (
    <div class="primary">
      <div class="container-horizontal">
        <BlockContent blocks={body}/>
        {pressphotos && 
          <div class="container-vetical">
            <h2>Press photos</h2>
            {pressphotos.map(pressphoto => 
            <a href={pressphoto.url}>
            <img class="item" src={urlFor(pressphoto).width(250).url()}/>
            </a>
            )}
         </div> 
        }
      </div>
    </div>
  );
}

function urlFor (source) {
  return imageUrlBuilder(client).image(source)
}


const query = groq`*[title == "EPK"][0]{
  title,
  body,
  pressphotos

}`;

EPK.getInitialProps = async function (context) {
  // It's important to default the slug so that it doesn't return "undefined"
  const { slug = "" } = context.query;
  return await client.fetch(query, {slug});
}

export default EPK;
